import React, { useState } from "react";
import Lottie from "lottie-react";
import myAnimation from "./heroAnimation.json";
import "./Projects.css";

const projectsData = [
  {
    id: 1,
    title: "Portfolio Website",
    description: "A fully responsive personal portfolio built with React.js.",
    link: "https://yourportfolio.com",
  },
  {
    id: 2,
    title: "Weather App",
    description: "A React-based weather app that fetches live weather data.",
    link: "https://yourweatherapp.com",
  },
  {
    id: 3,
    title: "Task Manager",
    description: "A simple task tracker to manage daily activities.",
    link: "https://yourtaskmanager.com",
  },
  {
    id: 4,
    title: "Project 4",
    description: "A sample project description",
    link: "https://yourproject4.com",
  },
];

const Projects = () => {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  const nextProject = () => {
    setCurrentProjectIndex((prevIndex) =>
      prevIndex === projectsData.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevProject = () => {
    setCurrentProjectIndex((prevIndex) =>
      prevIndex === 0 ? projectsData.length - 1 : prevIndex - 1
    );
  };

  const currentProject = projectsData[currentProjectIndex];

  return (
    <section id="projects" className="projects-section">
      {/* Left: Lottie Animation */}
      <div className="projects-lottie">
        <Lottie 
          animationData={myAnimation} 
          loop={true} 
          autoPlay={true}
          style={{ width: 500, height: 500 }}
        />
      </div>

      {/* Right: Featured Project */}
      <div className="projects-content">
        <h2>Featured Project</h2>

        {/* Project Card */}
        <div className="featured-project-card">
          <h3>{currentProject.title}</h3>
          <p>{currentProject.description}</p>
          <div className="project-buttons">
            <a href={currentProject.link} target="_blank" rel="noopener noreferrer" className="view-project-btn">
              View Project
            </a>
            <a href="/projects" className="see-all-btn">See All Projects</a>
          </div>
        </div>

        {/* Navigation Buttons */}
        <div className="project-navigation">
          <button className="nav-btn" onClick={prevProject}>◀</button>
          <button className="nav-btn" onClick={nextProject}>▶</button>
        </div>
      </div>
    </section>
  );
};

export default Projects;
