import React from "react";
import { motion } from "framer-motion";
import "./Hero.css";

// Define motion variants for container and items
const containerVariants = {
  hidden: {
    opacity: 0,
    x: -100,
  },
  visible: {
    opacity: 1,
    x: 0,
    transition: {
      // Stagger each child by 0.2 seconds
      staggerChildren: 0.2,
      // Once the container starts, animate over 0.8 seconds
      duration: 0.8,
      type: "spring",
      stiffness: 50,
    },
  },
};

const itemVariants = {
  hidden: {
    opacity: 0,
    y: 30,
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      ease: "easeOut",
    },
  },
};

const Hero = () => {
  return (
    <motion.section
      className="hero"
      // Set initial and animate states for the container
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <div className="hero-content">
        <motion.h1 className="hero-title" variants={itemVariants}>
          Rahsan Lewis
        </motion.h1>

        <motion.h2 className="hero-subtitle" variants={itemVariants}>
          Full Stack Developer
        </motion.h2>

        <motion.p className="hero-description" variants={itemVariants}>
          I specialize in creating responsive and interactive web applications
        </motion.p>
      </div>
    </motion.section>
  );
};

export default Hero;
