import React from "react";
import { ReactTyped } from "react-typed";
import "./Hero.css";

const Hero = () => {
  const projects = [
    {
      title: "Portfolio Website",
      description: "A fully responsive portfolio.",
      link: "https://yourportfolio.com",
    },
    {
      title: "Weather App",
      description: "Live weather updates with API data.",
      link: "https://yourweatherapp.com",
    },
    {
      title: "Task Manager",
      description: "A simple app to track tasks.",
      link: "https://yourtaskmanager.com",
    },
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <h1>Rahsan Lewis</h1>
        <h2>Full Stack Developer</h2>
        <p>
          I specialize in building responsive, user-friendly web applications
          using React.js. On the backend, I create secure and scalable APIs with
          Node.js, Express, and both SQL and NoSQL databases. I have a strong
          focus on code quality and modern development tools. I have strong
          problem-solving skills and the ability to thrive in collaborative,
          Agile environments making me a valuable addition to any development
          team.
        </p>

        {/* Animated Typing Effect */}
        <h3 className="animated-text">
          Check out my{" "}
          <ReactTyped
            strings={projects.map((p) => p.title)}
            typeSpeed={80}
            backSpeed={50}
            loop
          />
        </h3>

        <a href="#projects" className="cta-button">
          View My Work
        </a>
      </div>
    </section>
  );
};

export default Hero;
