import React from "react";
import Slider from "react-slick";
import ReactTyped from "react-typed";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Hero.css";

const Hero = () => {
  const projects = [
    { title: "Portfolio Website", description: "A fully responsive portfolio.", link: "https://yourportfolio.com" },
    { title: "Weather App", description: "Live weather updates with API data.", link: "https://yourweatherapp.com" },
    { title: "Task Manager", description: "A simple app to track tasks.", link: "https://yourtaskmanager.com" },
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <h1>Hi, I'm Your Name</h1>
        <h2>Frontend Developer | React Enthusiast</h2>

        {/* Animated Typing Effect */}
        <h3 className="animated-text">
          Check out my <ReactTyped strings={projects.map(p => p.title)} typeSpeed={80} backSpeed={50} loop />
        </h3>

        {/* Project Carousel */}
        <Slider {...settings} className="project-slider">
          {projects.map((project, index) => (
            <div key={index} className="slide">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer" className="project-link">
                View Project
              </a>
            </div>
          ))}
        </Slider>

        <a href="#projects" className="cta-button">View My Work</a>
      </div>
    </section>
  );
};

export default Hero;
