import React from "react";
import "./About.css"; // Import CSS file for styling

// // Import Custom Icons
// import HtmlIcon from "../assets/icons/html-icon.svg";
// import CssIcon from "../assets/icons/css-icon.svg";
// import JsIcon from "../assets/icons/js-icon.svg";
// import ReactIcon from "../assets/icons/react-icon.svg";
// import GitIcon from "../assets/icons/git-icon.svg";
// import ResponsiveIcon from "../assets/icons/responsive-icon.svg";

const About = () => {
  return (
    <section id="about" className="about">
      {/* Left Column: About Content */}
      <div className="about-content">
        <h2>About Me</h2>
        <p>
          I'm a passionate Frontend Developer with a strong focus on building
          interactive and responsive web applications using React.js.
        </p>
      </div>

      {/* Right Column: Skills List */}
      <div className="skills-list">
        <h3>Skills</h3>
        <ul>
          <li>
            <img src={HtmlIcon} alt="HTML" className="skill-icon" />
            HTML
          </li>
          <li>
            <img src={CssIcon} alt="CSS" className="skill-icon" />
            CSS
          </li>
          <li>
            <img src={JsIcon} alt="JavaScript" className="skill-icon" />
            JavaScript
          </li>
          <li>
            <img src={ReactIcon} alt="React.js" className="skill-icon" />
            React.js
          </li>
          <li>
            <img src={GitIcon} alt="Git & GitHub" className="skill-icon" />
            Git & GitHub
          </li>
          <li>
            <img src={ResponsiveIcon} alt="Responsive Design" className="skill-icon" />
            Responsive Design
          </li>
        </ul>
      </div>
    </section>
  );
};

export default About;
