import React from "react";
import { ReactTyped } from "react-typed";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./Hero.css";

const Hero = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <section className="hero">
      {/* Particle Background */}
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: { color: "#121212" },
          particles: {
            number: { value: 50 },
            move: { enable: true, speed: 2 },
            color: { value: "#00ffcc" },
            shape: { type: "circle" },
            opacity: { value: 0.5 },
            size: { value: 3 },
            links: { enable: true, distance: 150, color: "#00ffcc", opacity: 0.4, width: 1 },
          },
        }}
      />

      {/* Hero Content */}
      <div className="hero-content">
        <h1>Rahsan Lewis</h1>
        <h2>Full Stack Developer</h2>
        <p>I specialize in creating responsive and interactive web applications</p>
      </div>
    </section>
  );
};

export default Hero;
