import React from "react";
import Lottie from "lottie-react";
import myAnimation from "./Animation2.json"; // or wherever your JSON is located
import "./Projects.css";

const projectsData = [
  {
    id: 1,
    title: "Portfolio Website",
    description: "A fully responsive personal portfolio built with React.js.",
    link: "https://yourportfolio.com",
  },
  {
    id: 2,
    title: "Weather App",
    description: "A React-based weather app that fetches live weather data.",
    link: "https://yourweatherapp.com",
  },
  {
    id: 3,
    title: "Task Manager",
    description: "A simple task tracker to manage daily activities.",
    link: "https://yourtaskmanager.com",
  },
  {
    id: 4,
    title: "Project 4",
    description: "A simple task tracker to manage daily activities.",
    link: "https://yourtaskmanager.com",
  },
];

const Projects = () => {
  return (
    <section id="projects" className="projects-section">
      {/* Left: Lottie Animation */}
      <div className="projects-lottie">
        <Lottie 
          animationData={myAnimation} 
          loop={true} 
          autoPlay={true}
          style={{ width: 400, height: 400 }}
        />
      </div>

      {/* Right: Project Cards */}
      <div className="projects-content">
        <h2>My Projects</h2>
        <div className="projects-container">
          {projectsData.map((project) => (
            <div key={project.id} className="project-card">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer">
                View Project
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
