import React from "react";
import { Lottie } from "lottie-react";
import heroAnimation from "./heroAnimation.json"; // <-- use a relative path
import "./Hero.css";

const Hero = () => {
  return (
    <section className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Rahsan Lewis</h1>
        <h2 className="hero-subtitle">Full Stack Developer</h2>
        <p className="hero-description">
          I specialize in creating responsive and interactive web applications
        </p>
      </div>

      <Lottie
        animationData={heroAnimation}  // <-- make sure names match
        loop={true}
        autoPlay={true}
        style={{ width: 300, height: 300 }}
      />
    </section>
  );
};

export default Hero;
