import React, { useState } from "react";

const Counter = () => {
  const [count, setCount] = useState(0); // Declare state with initial value 0

  const increment = () => {
    setCount(count + 1); // Update state
  };

  return (
    <div>
      <h3>Counter: {count}</h3>
      <button onClick={increment}>Increase</button>
    </div>
  );
};

export default Counter;
