import React from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import heroAnimation from "./heroAnimation.json";
import "./Hero.css";

// Framer Motion Variants (as before)
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.6, ease: "easeOut" },
  },
};

const arrowVariants = {
  initial: { y: 0 },
  hover: { y: -5 },
  animate: {
    y: [0, 10, 0],
    transition: {
      duration: 1.5,
      repeat: Infinity,
      repeatType: "loop",
    },
  },
};

const Hero = () => {
  return (
    <section className="hero">
      {/* Main Hero Content in its own container */}
      <div className="hero-inner">
        <motion.div
          className="hero-content"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h1 className="hero-title" variants={itemVariants}>
            Rahsan Lewis
          </motion.h1>
          <motion.h2 className="hero-subtitle" variants={itemVariants}>
            Full Stack Developer
          </motion.h2>
          <motion.p className="hero-description" variants={itemVariants}>
            I specialize in creating responsive and interactive web applications
          </motion.p>
        </motion.div>

        <Lottie
          animationData={heroAnimation}
          loop={true}
          autoPlay={true}
          style={{ width: 500, height: 500 }}
        />
      </div>

      {/* Arrow Container: Full-Width, absolutely positioned */}
      <motion.div
        className="arrow-container"
        variants={arrowVariants}
        initial="initial"
        animate="animate"
        whileHover="hover"
      >
        <p className="scroll-text">View My Projects</p>
        <a href="#projects" aria-label="Scroll to projects">
          <svg
            width="36"
            height="36"
            fill="currentColor"
            viewBox="0 0 16 16"
          >
            <path
              fillRule="evenodd"
              d="M1.646 6.646a.5.5 0 0 1 .708 0L8
                12.293l5.646-5.647a.5.5 0 0 1
                .708.708l-6 6a.5.5 0 0 1-.708
                0l-6-6a.5.5 0 0 1 0-.708z"
            />
          </svg>
        </a>
      </motion.div>
    </section>
  );
};

export default Hero;
