import React from "react";
import Lottie from "lottie-react";
import myAnimation from "./heroAnimation.json";
import Slider from "react-slick";
import "./Projects.css";

const projectsData = [
  {
    id: 1,
    title: "Portfolio Website",
    description: "A fully responsive personal portfolio built with React.js.",
    link: "https://yourportfolio.com",
  },
  {
    id: 2,
    title: "Weather App",
    description: "A React-based weather app that fetches live weather data.",
    link: "https://yourweatherapp.com",
  },
  {
    id: 3,
    title: "Task Manager",
    description: "A simple task tracker to manage daily activities.",
    link: "https://yourtaskmanager.com",
  },
  {
    id: 4,
    title: "Project 4",
    description: "A sample project description",
    link: "https://yourproject4.com",
  },
];

const Projects = () => {
  // Slick Slider settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerMode: false,  // Disable centerMode
    centerPadding: "0", // Ensures no extra padding on the sides
    adaptiveHeight: true, // Ensures height adjusts dynamically
  };  


  return (
    <section id="projects" className="projects-section">
      {/* Left: Lottie Animation */}
      <div className="projects-lottie">
        <Lottie 
          animationData={myAnimation} 
          loop={true} 
          autoPlay={true}
          style={{ width: 500, height: 500 }}
        />
      </div>

      {/* Right: Carousel of Projects */}
      <div className="projects-content">
        <h2>My Projects</h2>

        <Slider {...settings} className="projects-slider">
          {projectsData.map((project) => (
            <div key={project.id}>
              <div className="project-card">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <a 
                  href={project.link} 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  View Project
                </a>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </section>
  );
};

export default Projects;
