import React from "react";
import { ReactTyped } from "react-typed";
import Particles from "react-tsparticles";
import "./Hero.css";

const Hero = () => {
  return (
    <section className="hero">
      {/* Particle Background */}
      <Particles
        id="tsparticles"
        options={{
          background: { color: "#121212" },
          fpsLimit: 60,
          interactivity: {
            events: {
              onHover: { enable: true, mode: "repulse" },
              onClick: { enable: true, mode: "push" },
            },
            modes: {
              repulse: { distance: 100 },
              push: { quantity: 4 },
            },
          },
          particles: {
            number: { value: 80, density: { enable: true, area: 800 } },
            move: { enable: true, speed: 2 },
            color: { value: "#00ffcc" },
            shape: { type: "circle" },
            opacity: { value: 0.5 },
            size: { value: 3 },
            links: { enable: true, distance: 150, color: "#00ffcc", opacity: 0.4, width: 1 },
          },
        }}
        style={{
          position: "absolute",
          width: "100%",
          height: "100%",
          top: 0,
          left: 0,
          zIndex: -1,
        }}
      />

      {/* Hero Content */}
      <div className="hero-content">
        <h1>Rahsan Lewis</h1>
        <h2>Full Stack Developer</h2>
        <p>I specialize in creating responsive and interactive web applications</p>
      </div>
    </section>
  );
};

export default Hero;
