import React from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import HeroAnimation from "./HeroAnimation.json";
import "./Hero.css";

const scrollToSkills = () => {
  const skillsSection = document.getElementById("skills");
  if (skillsSection) {
    skillsSection.scrollIntoView({ behavior: "smooth" });
  }
};

// Framer Motion Variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      // Stagger each child element by 0.2s
      staggerChildren: 0.2,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.6, ease: "easeOut" },
  },
};

// Arrow bounce variants (if desired)
const arrowVariants = {
  initial: { y: 0 },
  hover: { y: -5 },
  animate: {
    y: [0, 10, 0],
    transition: {
      duration: 1.5,
      repeat: Infinity,
      repeatType: "loop",
    },
  },
};

const Hero = () => {
  return (
    <section className="hero">
      {/* Text + arrow in one container */}
      <motion.div
        className="hero-content"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.h1 className="hero-title" variants={itemVariants}>
          Rahsan Lewis
        </motion.h1>

        <motion.h2 className="hero-subtitle" variants={itemVariants}>
          Full Stack Developer
        </motion.h2>

        <motion.p className="hero-description" variants={itemVariants}>
          I design, develop, and deploy full-stack applicationsâ€”from elegant
          frontend interfaces to powerful backend APIs & databases. Whether it's
          React, Node.js, or cloud deployments, I bring ideas to life with
          clean, efficient code.
        </motion.p>

        {/* Clickable Button for Scrolling */}
        <motion.button className="scroll-button" onClick={scrollToSkills}>
          <span className="scroll-text">View My Skills</span>
            <path
              fillRule="evenodd"
              d="M1.646 6.646a.5.5 0 0 1 .708 0L8
                  12.293l5.646-5.647a.5.5 0 0 1
                  .708.708l-6 6a.5.5 0 0 1-.708
                  0l-6-6a.5.5 0 0 1 0-.708z"
            />
        </motion.button>
      </motion.div>

      {/* Lottie animation can remain on the side */}
      <Lottie
        animationData={HeroAnimation}
        loop={true}
        autoPlay={true}
        style={{ width: 500, height: 560 }}
      />
    </section>
  );
};

export default Hero;
