import React from "react";
import { ReactTyped } from "react-typed"; // Ensure the correct import with {}
import "./Hero.css";

const Hero = () => {
  const projects = [
    {
      title: "Portfolio Website",
      description: "A responsive portfolio to showcase my work.",
      link: "https://yourportfolio.com",
    },
    {
      title: "Weather App",
      description: "Live weather updates using React and an API.",
      link: "https://yourweatherapp.com",
    },
    {
      title: "Task Tracker",
      description: "A simple app to manage daily tasks.",
      link: "https://yourtasktracker.com",
    },
    {
      title: "E-Commerce Store",
      description: "A mock e-commerce platform with React.",
      link: "https://yourecommerce.com",
    },
  ];

  return (
    <section className="hero">
      <div className="hero-content">
        <h1>Rahsan Lewis</h1>
        <h2>Full Stack Developer</h2>
        <p>I specialize in creating responsive and interactive web applications using React.js.</p>

        {/* React Typed Component */}
        <h3 className="animated-text">
          Check out my{" "}
          <ReactTyped
            strings={projects.map((project) => project.title)}
            typeSpeed={80}
            backSpeed={50}
            loop
          />
        </h3>

        {/* Project Grid */}
        <div className="project-grid">
          {projects.map((project, index) => (
            <div key={index} className="project-card">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer" className="project-link">
                View Project
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Hero;
