import React from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import HeroAnimation from "./HeroAnimation.json";
import "./Hero.css";

// Function to handle smooth scrolling
const scrollToSkills = () => {
  const skillsSection = document.getElementById("skills");
  if (skillsSection) {
    skillsSection.scrollIntoView({ behavior: "smooth" });
  }
};

const Hero = () => {
  return (
    <section className="hero">
      {/* Hero Content */}
      <motion.div className="hero-content" initial="hidden" animate="visible">
        <motion.h1 className="hero-title">Rahsan Lewis</motion.h1>
        <motion.h2 className="hero-subtitle">Full Stack Developer</motion.h2>
        <motion.p className="hero-description">
          I design, develop, and deploy full-stack applicationsâ€”from elegant
          frontend interfaces to powerful backend APIs & databases. Whether it's
          React, Node.js, or cloud deployments, I bring ideas to life with
          clean, efficient code.
        </motion.p>

        {/* Clickable Button for Scrolling */}
        <motion.button className="scroll-button" onClick={scrollToSkills}>
          <span className="scroll-text">View My Skills</span>
          <svg width="36" height="36" fill="currentColor" viewBox="0 0 16 16">
            <path
              fillRule="evenodd"
              d="M1.646 6.646a.5.5 0 0 1 .708 0L8
                  12.293l5.646-5.647a.5.5 0 0 1
                  .708.708l-6 6a.5.5 0 0 1-.708
                  0l-6-6a.5.5 0 0 1 0-.708z"
            />
          </svg>
        </motion.button>
      </motion.div>

      {/* Lottie Animation */}
      <Lottie animationData={HeroAnimation} loop={true} autoPlay={true} style={{ width: 500, height: 560 }} />
    </section>
  );
};

export default Hero;
