import React from "react";
import Lottie from "lottie-react";
import myAnimation from "./heroAnimation.json";
import "./Projects.css";

const projectsData = [
  {
    id: 1,
    title: "Portfolio Website",
    description: "A fully responsive personal portfolio built with React.js.",
    link: "https://yourportfolio.com",
  },
  {
    id: 2,
    title: "Weather App",
    description: "A React-based weather app that fetches live weather data.",
    link: "https://yourweatherapp.com",
  },
  {
    id: 3,
    title: "Task Manager",
    description: "A simple task tracker to manage daily activities.",
    link: "https://yourtaskmanager.com",
  },
  {
    id: 4,
    title: "Project 4",
    description: "A sample project description",
    link: "https://yourproject4.com",
  },
];

const Projects = () => {
  const scrollLeft = () => {
    document.querySelector(".projects-scroll-container").scrollBy({
      left: -300,
      behavior: "smooth",
    });
  };

  const scrollRight = () => {
    document.querySelector(".projects-scroll-container").scrollBy({
      left: 300,
      behavior: "smooth",
    });
  };

  return (
    <section id="projects" className="projects-section">
      {/* Left: Lottie Animation */}
      <div className="projects-lottie">
        <Lottie 
          animationData={myAnimation} 
          loop={true} 
          autoPlay={true}
          style={{ width: 500, height: 500 }}
        />
      </div>

      {/* Right: Horizontal Scroll Projects */}
      <div className="projects-content">
        <h2>My Projects</h2>

        {/* Scroll Buttons */}
        <div className="projects-navigation">
          <button className="scroll-btn" onClick={scrollLeft}>⬅</button>
          <button className="scroll-btn" onClick={scrollRight}>➡</button>
        </div>

        {/* Horizontal Scroll Container */}
        <div className="projects-scroll-container">
          <div className="projects-row">
            {projectsData.map((project) => (
              <div key={project.id} className="project-card">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <a href={project.link} target="_blank" rel="noopener noreferrer">
                  View Project
                </a>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
