import React, { useState } from "react";
import Header from "./Header";
import Counter from "./Counter";
import ProjectList from "./ProjectList";

const App = () => {
  const [projects, setProjects] = useState([
    { id: 1, name: "Portfolio Website" },
    { id: 2, name: "Weather App" },
    { id: 3, name: "Task Tracker" },
  ]);

  const [newProject, setNewProject] = useState("");

  const handleAddProject = (e) => {
    e.preventDefault();
    if (newProject.trim() === "") return;

    setProjects([
      ...projects,
      { id: projects.length + 1, name: newProject.trim() },
    ]);
    setNewProject(""); // Clear the input
  };

  const handleRemoveProject = (id) => {
    setProjects(projects.filter((project) => project.id !== id));
  };

  return (
    <div>
      <Header title="My React Portfolio" subtitle="Learning React Step by Step" />
      <p>Welcome to my first React app!</p>
      <Counter />
      <h3>My Projects</h3>
      <form onSubmit={handleAddProject}>
        <input
          type="text"
          placeholder="New Project Name"
          value={newProject}
          onChange={(e) => setNewProject(e.target.value)}
        />
        <button type="submit">Add Project</button>
      </form>
      <ProjectList projects={projects} onRemove={handleRemoveProject} />
    </div>
  );
};

export default App;
